import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      selectedCities: []
    }
    this.addCity = this.addCity.bind(this)
    this.removeCity = this.removeCity.bind(this)
  }

  addCity(event) {
    let city = event.target.innerText
    this.setState(prevState => ({
      selectedCities: [...prevState.selectedCities, city]
    }))    
  }
  removeCity(event) {
    var array = [...this.state.people]; // make a separate copy of the array
    var index = array.indexOf(e.target.value)
    if (index !== -1) {
        array.splice(index, 1);
        this.setState({
          people: array
        });
    }
    console.warn(this.state.selectedCities.indexOf(event.target.innerText))
    this.setState({
      selectedCities: this.state.selectedCities.splice(
        this.state.selectedCities.indexOf(event.target.innerText), 1
      )
    })
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            cities={this.state.selectedCities}
            addCity={this.addCity}
            removeCity={this.removeCity}
          />
          <Info
            cities={this.state.selectedCities}
          />
        </header>
      </div>
    )
  }
}
export default App
