import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      data: []
    }
    this.addData = this.addData.bind(this)
    this.removeData = this.removeData.bind(this)
  }
  addData(name, data) {
    console.warn(name, data)
    // check if the state was already added
    if(this.state.cities.indexOf(name) !== -1) return
    let newElement = {
      city: name,
      temperature: data.Temperature,
      dawn: data.Dawn,
      dusk: data.Dusk,
      color: "rgba(255,0,0,255)"
    }
    this.setState(prevState => ({
      data: [...prevState.data, newelement]
    }))
  }
  removeData(val) {
    this.setState({
      cities: this.state.cities.filter(function(city) { 
        return city !== val
      })
    })
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            cities={this.state.cities}
            addCity={this.addData}
            removeCity={this.removeData}
          />
          <Info
            cities={this.state.cities}
            temperatures={this.state.temperatures}
            dawn={this.state.dawn}
            dusk={this.state.dusk}
            color={this.state.color}
          />
        </header>
      </div>
    )
  }
}
export default App
