import React from 'react'
import Chart from 'chart.js/auto'
import { Bar } from "react-chartjs-2"

class TemperatureGraph extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            cities: [],
            temperature: [],
            color: []
        }
    }
    updateData(){
        console.warn("DAAAATA", this.props.data)
        let citiesArray = []
        let tempArray = []
        let colorArray = []
        this.props.data.map( (data) => {
            citiesArray.push(data.city)
            tempArray.push(data.temperature)
            colorArray.push(data.COLOR)
        })
        this.setState({
            cities: citiesArray
        })
    }

    render () {
        this.updateData()
        return (
            <div id="TemperatureGraph">
                <Bar
                    width={600}
                    height={400}
                    data={{
                        labels: this.state.cities,
                        datasets: [{
                            label: "Temperature per City",
                            data: this.state.temperature,
                            backgroundColor: this.state.color
                        }]
                    }}
                    options={{
                        maintainAspectRatio: false,
                        hover: {
                            mode: false
                        }
                    }}
                />
            </div>
        )
    }
}
export default TemperatureGraph
