import React from 'react'
import "./SearchBar.css"
import SearchIcon from '@material-ui/icons/Search'
import CloseIcon from '@material-ui/icons/Close';

class SearchBar extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            inputValue: "",
            suggestions: {},
            selectedCities: []
        }
        this.updateInput = this.updateInput.bind(this)
        this.addCity = this.addCity.bind(this)
        this.removeCity = this.removeCity.bind(this)
    }
    updateInput(event){
        let val = event.target.value
        this.setState({
            inputValue: val
        })
        setTimeout(() => {
            this.getSuggestions()
        }, 500)
    }  
    getSuggestions() {
        let city = this.state.inputValue
        fetch(`http://localhost:2000/suggestions/${city}`).
            then( res => res.text() ).
            then( res => { 
                let response = JSON.parse(res)
                this.setState({ suggestions: response })
            }).catch(err => { this.setState({ suggestions: [] }) })
    }
    addCity(event) {
        let city = event.target.innerText
        this.setState( prevState => ({
            selectedCities: [...prevState.selectedCities, city],
            inputValue: "",
            suggestions: []
        }))
        console.warn("array => ", this.state.selectedCities)
    }
    removeCity(event) {
        this.setState({
            selectedCities: this.state.selectedCities.filter(
                function(city) { 
                    return city !== event.target.innerText 
                })
        })
        console.warn("array => ", this.state.selectedCities)
    }

    render() {
        return (
            <div className="search">
                <div className="searchInputs">
                    <input
                        type="text"
                        placeholder={ this.props.placeholder }                    
                        onChange={ this.updateInput }
                        value={ this.state.inputValue }
                    />
                    <div className="searchIcon">
                        <SearchIcon/>
                    </div>
                </div>
                <SuggestionsList suggestions={this.state.suggestions} handleClick={this.addCity}/>
                <SelectedCities cities={this.state.selectedCities} removeCity={this.removeCity}/>
            </div>
        )
    }
}

function SuggestionsList(props) {
    if (Object.keys(props.suggestions).length == 0) {
        return null
    } else {
        return (
            <div className="dataResult">
                {props.suggestions.map((value) => {
                    return (
                        <a className="dataItem" key={value.Key} onClick={props.handleClick}>
                            <p> {value.LocalizedName} </p>
                            <p className="country"> {value.Country.LocalizedName} </p>
                        </a>
                    )
                })}
            </div>
        )
    }
}
function SelectedCities(props) {
    if(props.cities.length == 0) {
        return null
    } else {
        return (
            <div className="citiesPoolContainer">
                {props.cities.map((city) => {
                    return (
                        <div className="cityContainer" key={city}>
                            <div className="cityName">{city}</div>
                            <CloseIcon className="cross" onClick={props.removeCity}/>
                        </div>
                    )
                })}
            </div>
        )
    }
}
export default SearchBar
