import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      data: {
        selectedCities: []
      }
    }
  }
  addCity(event) {
    let city = event.target.innerText
    this.props.data.selectedCities.push(city)
    this.setState({
        inputValue: "",
        suggestions: []
    })
    setTimeout( ()=> {console.warn("cities", this.props.data.selectedCities)}, 2000 )
  }
  removeCity() {
    this.setState({
      data: {
        selectedCities: this.props.selectedCities.filter(
          function(city) {
              return city !== event.target.innerText 
          }
        )
      }
  })
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            data={this.state.data}
            addCity={this.addCity}
            removecity={this.removeCity}
          />
        </header>
        <Info
          data={this.state.data}
        />
      </div>
    )
  }
}
export default App
