import React from 'react'
import SearchIcon from '@material-ui/icons/Search'

function SuggestionsList(props) {
    if(Object.keys(props.suggestions).length == 0) {
        console.warn(props.suggestions)
        props.suggestions.map((value, key) => {
            return (
                <a className="dataItem">
                    {value.LocalizedName}
                </a>
            )
        })
    } else {
        return null
    }
}

class SearchBar extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            inputValue: "",
            suggestions: {}
        }
        this.updateInput = this.updateInput.bind(this);
    }

    updateInput(event){
        let val = event.target.value
        this.setState({
            inputValue: val
        })
        setTimeout(() => {
            this.getSuggestions()
        }, 500)
    }

    getSuggestions() {
        let city = this.state.inputValue
        console.warn(city)
        fetch(`http://localhost:2000/suggestions/${city}`).
            then( res => res.text() ).
            then( res => { 
                let response = JSON.parse(res)
                this.setState({ suggestions: response }) 
            })
    }

    render() {
        return (
            <div className="search">
                <div className="searchInputs">
                    <input
                        type="text"
                        placeholder={ this.props.placeholder }                    
                        onChange={ this.updateInput }
                        value={ this.state.inputValue }
                    />
                    <div className="searchIcon">
                        <SearchIcon/>
                    </div>
                </div>
                <div className="dataResult">
                    <SuggestionsList suggestions={this.state.suggestions} />
                </div>
            </div>
        )
    }
}
export default SearchBar
