import React from 'react'
import Chart from 'chart.js/auto'
import { Bar } from "react-chartjs-2"

class TemperatureGraph extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            cities: [],
            temperature: [],
            color: []
        }
    }
    updateData(){
        this.props.data.map( (data) => {
            this.setState( prevState => ({
                cities: [...prevState.cities, data.city],
                temperature: [...prevState.temperature, data.temperature],
                color: [...prevState.color, data.color]
            }))
        })
    }

    render () {    
        return (
            <div id="TemperatureGraph">
                <Bar
                    width={600}
                    height={400}
                    data={{
                        labels: this.state.cities,
                        datasets: [{
                            label: "Temperature per City",
                            data: this.state.temperature,
                            backgroundColor: this.state.color
                        }]
                    }}
                    options={{
                        maintainAspectRatio: false,
                        hover: {
                            mode: false
                        }
                    }}
                />
            </div>
        )
    }
}
export default TemperatureGraph
