import React from 'react'
import "./SearchBar.css"
import CloseIcon from '@material-ui/icons/Close';

class SelectedCities extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            city: "",
            cities: props.cities
        }
        this.removeCity = this.removeCity.bind(this)
    }
    removeCity(event) {
        this.setState({
            selectedCities: this.state.selectedCities.filter(
                function(city) { 
                    console.warn(city, event.target.innerText)
                    console.log(city !== event.target.innerText)
                    return city !== event.target.innerText 
                })
        })
        console.warn("array => ", this.state.selectedCities)
    }
    render() {
        return (
            <div className="citiesPoolContainer">

                {this.state.cities.map((city) => {
                    <CityContainer city={city} removeCity={this.removeCity}/>
                })}
            </div>
        )
    }
}
function CityContainer(props) {
    return (
        <div className="cityContainer" key={props.city}>
            <div className="cityName">{props.city}</div>
            <CloseIcon className="cross" onClick={props.removeCity}/>
        </div>
    )
}
export default SelectedCities
