import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      selectedCities: []
    }
    this.addCity = this.addCity.bind(this)
    this.removeCity = this.removeCity.bind(this)
  }

  addCity(val) {
    this.setState(prevState => ({
      selectedCities: [...prevState.selectedCities, val]
    }))    
  }
  removeCity(event) {
    console.error("event => ", event)
    let val = event.target.innerText
    let index = this.state.selectedCities.indexOf(val)
    console.warn("val => ", val)
    console.warn("index => ", index)
    console.warn("cities => ", this.state.selectedCities)
    if (index !== -1) {
      let array = this.state.selectedCities.splice(index, 1)  
      this.setState({
        selectedCities: array
      })
    }
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            cities={this.state.selectedCities}
            addCity={this.addCity}
            removeCity={this.removeCity}
          />
          <Info
            cities={this.state.selectedCities}
          />
        </header>
      </div>
    )
  }
}
export default App
