import React from 'react'
import Chart from 'chart.js/auto'
import { Bar } from "react-chartjs-2"

class TemperatureGraph extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            cities: []
        }
    }
    updateData(){
        let array = []
        this.props.data.map( (data) => {
            console.warn("d => ",data.city)
            array.push(data.city)
        })
        console.warn(array)
        this.setState({
            cities: array
        })
    }
    componentDidUpdate(){
        this.getCities()
    }
    render () {
        this.updateData()
        return (
            <div id="TemperatureGraph">
                <Bar
                    width={600}
                    height={400}
                    data={{
                        labels: this.getCities,
                        datasets: [{
                            label: "Temperature per City",
                            data: this.props.temperatures,
                            backgroundColor: this.props.color
                        }]
                    }}
                    options={{
                        maintainAspectRatio: false,
                        hover: {
                            mode: false
                        }
                    }}
                />
            </div>
        )
    }
}
export default TemperatureGraph
