    render () {
        console.warn(this.props.cities)
        return (
            <div id="TemperatureGraph">
                <Bar
                    width={600}
                    height={400}
                    data={{
                        labels: this.props.cities,
                        datasets: [{
                            label: "Temperature per City",
                            data: this.props.temperatures,
                            backgroundColor: this.props.color
                        }]
                    }}
                    options={{
                        maintainAspectRatio: false,
                        hover: {
                            mode: false
                        }
                    }}
                />
            </div>
        )
    }
}
export default TemperatureGraph
