import React from 'react'
import "./SearchBar.css"
import CloseIcon from '@material-ui/icons/Close'

class SearchBar extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            inputValue: "",
            suggestions: {}
        }
        this.updateInput = this.updateInput.bind(this)
        this.addCity = this.addCity.bind(this)
        this.removeCity = this.removeCity.bind(this)
    }
    updateInput(event){
        let val = event.target.value
        this.setState({
            inputValue: val
        })
        this.getSuggestions()
    }  
    async getSuggestions() {
        let city = this.state.inputValue
        console.warn("city ", city)
        const response = await fetch(`http://localhost:2000/suggestions/${city}`).
            then( res => res.text() )
        const json = await JSON.parse(response)
        return json
    }
    async getCityData(key) {
        const response = await fetch(`http://localhost:2000/cities/${key}`).
            then( res => res.text() )
        const json = await JSON.parse(response)
        return json
    }
    addCity(val) {
        // clean search bar and sugestions
        this.setState({
            inputValue: "",
            suggestions: []
        })
        let data = this.getCityData(val.Key)
        console.warn("addcity ===> ", data)
        this.props.addCity(val)
    }
    removeCity(event) {
        this.props.removeCity(event)
    }
    render() {
        return (
            <div className="search">
                <div className="searchInputs">
                    <input
                        type="text"
                        placeholder={ this.props.placeholder }                    
                        onChange={ this.updateInput }
                        value={ this.state.inputValue }
                    />
                </div>
                <SuggestionsList suggestions={this.state.suggestions} handleClick={this.addCity}/>
                <SelectedCities cities={this.props.cities} removeCity={this.removeCity}/>
            </div>
        )
    }
}
function SuggestionsList(props) {
    if (Object.keys(props.suggestions).length == 0) {
        return null
    } else {
        return (
            <div className="dataResult">
                {props.suggestions.map((value) => {
                    return (
                        <a className="dataItem" key={value.Key} onClick={() => {props.handleClick(value)} }>
                            <p> {value.LocalizedName} </p>
                            <p className="country"> {value.Country.LocalizedName} </p>
                        </a>
                    )
                })}
            </div>
        )
    }
}
function SelectedCities(props) {
    return (
    <div className="citiesPoolContainer">
        {props.cities.map((city) => {
            return (
                <div className="cityContainer" key={city}>
                    <div className="cityName"> {city} </div>
                    <CloseIcon className="cross" onClick={() => {props.removeCity(city)} }/>
                </div>
            )
        })}
    </div>
    )
}
export default SearchBar
