import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      cities: [],
      selectedCities: [],
      temperatures: [],
      dawn: [],
      dusk: []
    }
    this.addData = this.addData.bind(this)
    this.removeData = this.removeData.bind(this)
  }
  addData(val) {
    let newCity = {
      country: val.Country.LocalizedName,
      name: val.LocalizedName,
    }
    this.setState(prevState => ({
      selectedCities: [...prevState.cities, val.LocalizedName]
    }))    
  }
  removeData(val) {
    this.setState({
      selectedCities: this.state.selectedCities.filter(function(city) { 
        return city !== val
      })
    })
  }
  render() {
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            cities={this.state.selectedCities}
            addCity={this.addData}
            removeCity={this.removeData}
          />
          <Info
            cities={this.state.selectedCities}
            temperatures={this.state.temperatures}
            dawn={this.state.dawn}
            dusk={this.state.dusk}
          />
        </header>
      </div>
    )
  }
}
export default App
