import React from 'react'
import './App.css'
import SearchBar from './components/SearchBar/SearchBar.js'
import Info from './components/Info/Info.js'

class App extends React.Component {
  constructor(props){
    super(props)
    this.state = {
      data: []
    }
    this.addData = this.addData.bind(this)
    this.removeData = this.removeData.bind(this)
  }
  addData(name, data) {
    let exists
    // check if the state was already added
    this.state.data.map( (d) => {
      exists = d.city.indexOf(name) !== -1
    })
    if(exists) return // exit the function if the user already selected a city that already exists

    let dawn = new Date(0)
    let time = new Intl.DateTimeFormat('en-US', { year: 'numeric', month: '2-digit', day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit' }).format(dawn)
    console.warn(time)

    /*let dusk = new Date(0)
    dusk = dusk.setUTCSeconds(data.Dusk)
    dusk = dusk.split(" ")*/

    let newElement = {
      city: name,
      temperature: data.Temperature,
      dawn: data.Dawn,
      dusk: data.Dusk,
      color: "rgba(255,0,0,255)"
    }
    this.setState(prevState => ({
      data: [...prevState.data, newElement]
    }))
  }
  removeData(val) {
    this.setState({
      data: this.state.data.filter(function(city) { 
        return city.city !== val
      })
    })
  }
  render() {
    {console.warn("APP INFO ==> ", this.state.data)}
    return (
      <div className="App">
        <header className="App-header">
          <SearchBar
            placeholder="Enter a City Name"
            data={this.state.data}
            addCity={this.addData}
            removeCity={this.removeData}
          />
          <Info
            data={this.state.data}
          />
        </header>
      </div>
    )
  }
}
export default App
